<?phpif (isset($_POST['n']) && isset($_POST['a']) && isset($_POST['b']) && isset($_POST['user_func'])){$n = $_POST['n'];$a = $_POST['a'];$b = $_POST['b'];$user_func = $_POST['user_func'];$h = ($b-$a)/$n;$sum1 = func($user_func, ($a+$h/2));$sum2 = 0;for ($i=1; $i<$n; $i++){	$sum1 = $sum1 + func($user_func, ($a+($h*$i)+($h/2)));	$sum2 = $sum2 + func($user_func, ($a+($h*$i)));}$out = ($h/6)*(func($user_func, $a)+func($user_func, $b)+(4*$sum1)+(2*$sum2));}else{$out='error: improper inputs';}function func($user_func, $x){	$fun_pos=strlen($user_func)-1;	while($fun_pos>=0){		if($user_func[$fun_pos]=='x'){			//dealing with exp and max (both function have x's in them			if($fun_pos>0 && $fun_pos<(strlen($user_func)-1)){				if($user_func[$fun_pos+1]!='p' && $user_func[$fun_pos-1]!='a'){					$user_func = substr_replace($user_func,"$",$fun_pos,0);				}			}else{$user_func = substr_replace($user_func,"$",$fun_pos,0);}		}		$fun_pos--;	}	eval("\$out=$user_func;");	return $out;}?>