<?php$symbol = 'goog';$quote = getquote($symbol);echo "$quote[0]<br>";echo "$quote[1]<br>";echo "$quote[2]<br>";echo "$quote[3]<br>";function getquote($symbol){    if (empty($symbol)) {        $symbol = 'dji';    }    $fields = 'snl1c1ohgvt1';        // we use yahoo to get the quotes    $host = 'http://finance.yahoo.com';    // construct the url    $url = $host . '/d/quotes.csv?s='. urlencode($symbol) .'&f='.$fields.'';    // ask yahoo for the .csv file    $csv = @file_get_contents($url);    // if we get the csv file    if ($csv) {        // explode the csv file into data pices and replace the " with nothing, we dont want any "'s        if ($quote = explode(',', str_replace('"', '', $csv))) {            $quote = array_map('trim', $quote);            // output the quote data            return $quote;        }    }/**************************         READMEs    =    Symbol    = $quote[0]n    =    Name    = $quote[1]l1    =    Last    = $quote[2]c1    =    Change    = $quote[3]o    =    Opening    = $quote[4]h    =    High    = $quote[5]g    =    Low        = $quote[6]v    =    Volume    = $quote[7]d1    =    Date    = $quote[8]t1    =    Time    = $quote[9]***************************/}  ?>